import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useLocation } from 'react-router-dom';
import ContactForm from './ContactForm';
import BlogForm from './BlogForm';
import BlogAdmin from './BlogAdmin';

// Simple Not Found component for invalid routes
const NotFound = () => {
  const location = useLocation();
  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h1 className="text-4xl font-bold text-gray-800 mb-4">404 - Page Not Found</h1>
      <p className="text-lg text-gray-600 mb-6">
        The page <span className="font-semibold">{location.pathname}</span> does not exist.
      </p>
      <Link
        to="/"
        className="px-6 py-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-all duration-300"
      >
        Go to Contact Form
      </Link>
    </div>
  );
};

// Navigation Bar component
const NavBar = () => {
  const location = useLocation();
  const navItems = [
    { path: '/', label: 'Contact Form' },
    { path: '/add-blog', label: 'Add Blog Post' },
    { path: '/blog-admin', label: 'Blog Admin' },
  ];

  return (
    <nav className="bg-gradient-to-r from-blue-500 to-purple-600 p-4 shadow-lg">
      <ul className="flex space-x-6 justify-center">
        {navItems.map((item) => (
          <li key={item.path}>
            <Link
              to={item.path}
              className={`text-white px-4 py-2 rounded-lg transition-all duration-300 ${
                location.pathname === item.path
                  ? 'bg-white/20 font-semibold'
                  : 'hover:bg-white/10'
              }`}
            >
              {item.label}
            </Link>
          </li>
        ))}
      </ul>
    </nav>
  );
};

// Main App component
const App = () => {
  return (
    <Router>
      {/* Navigation Bar */}
      <NavBar />

      {/* Main Content */}
      <div className="min-h-screen bg-gray-50">
        <Routes>
          <Route path="/" element={<ContactForm />} />
          <Route path="/contact" element={<ContactForm />} />
          <Route path="/add-blog" element={<BlogForm />} />
          <Route path="/blog-admin" element={<BlogAdmin />} />
          <Route path="*" element={<NotFound />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
