import React, { useState } from 'react';

const Resume = () => {
  const [isExpanded, setIsExpanded] = useState(false);

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-lg">
      <button
        onClick={() => setIsExpanded(!isExpanded)}
        className="w-full p-3 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition duration-300 mb-4"
      >
        {isExpanded ? 'Hide Details' : 'Show Details'}
      </button>
      {isExpanded && (
        <div className="space-y-6">
          <div>
            <h3 className="text-lg font-semibold text-gray-800">Education</h3>
            <p className="text-gray-600">[Your Education Details]</p>
          </div>
          <div>
            <h3 className="text-lg font-semibold text-gray-800">Experience</h3>
            <p className="text-gray-600">[Your Experience Details]</p>
          </div>
          <div>
            <h3 className="text-lg font-semibold text-gray-800">Skills</h3>
            <p className="text-gray-600">[Your Skills]</p>
          </div>
        </div>
      )}
      <div className="mt-4">
        <a
          href="/path-to-your-resume.pdf"
          download
          className="inline-block p-3 bg-green-600 text-white rounded-md hover:bg-green-700 transition duration-300"
        >
          Download PDF
        </a>
      </div>
    </div>
  );
};

export default Resume;
