import React, { useState } from 'react';
import { Helmet } from 'react-helmet';
import ProjectCard from './components/ProjectCard';
import Resume from './components/Resume';
import ContactForm from './components/ContactForm';

function App() {
  const [currentSection, setCurrentSection] = useState('cover');

  const projects = [
    { title: 'Project 1', description: 'A brief description of project 1.', link: 'https://example.com', image: '/project1.jpg' },
    { title: 'Project 2', description: 'A brief description of project 2.', link: 'https://example.com', image: '/project2.jpg' },
  ];

  const navItems = [
    { id: 'about', label: 'About Me' },
    { id: 'portfolio', label: 'Portfolio' },
    { id: 'resume', label: 'Resume' },
    { id: 'contact', label: 'Contact' },
  ];

  return (
    <div className="min-h-screen bg-gray-100">
      <Helmet>
        <title>[Your Name] | Personal Portfolio</title>
        <meta name="description" content="Personal portfolio of [Your Name], showcasing skills, projects, and achievements as a [Your Profession]." />
        <meta name="keywords" content="portfolio, [Your Name], [Your Profession], projects, skills, resume, contact" />
        <meta name="author" content="[Your Name]" />
        <meta name="robots" content="index, follow" />
        <meta property="og:title" content="[Your Name] | Personal Portfolio" />
        <meta property="og:description" content="Explore the portfolio of [Your Name], a [Your Profession] showcasing skills, projects, and achievements." />
        <meta property="og:type" content="website" />
        <meta property="og:url" content="https://yourwebsite.com" />
        <meta property="og:image" content="https://yourwebsite.com/og-image.jpg" />
      </Helmet>

      {/* Navigation Bar */}
      <nav className="fixed top-0 left-0 w-full bg-white shadow-md z-10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <h1 className="text-2xl font-bold text-gray-800">[Your Name]</h1>
            </div>
            <div className="flex items-center space-x-4">
              {navItems.map((item) => (
                <button
                  key={item.id}
                  onClick={() => setCurrentSection(item.id)}
                  className={`px-3 py-2 text-sm font-medium transition-colors duration-300 ${
                    currentSection === item.id
                      ? 'text-blue-600 border-b-2 border-blue-600'
                      : 'text-gray-600 hover:text-blue-600'
                  }`}
                >
                  {item.label}
                </button>
              ))}
              {currentSection !== 'cover' && (
                <button
                  onClick={() => setCurrentSection('cover')}
                  className="px-3 py-2 text-sm font-medium text-gray-600 hover:text-blue-600 transition-colors duration-300"
                >
                  Home
                </button>
              )}
            </div>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="pt-16">
        {/* Cover Page */}
        {currentSection === 'cover' && (
          <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-500 to-purple-600">
            <div className="text-center text-white">
              <h1 className="text-5xl md:text-7xl font-bold mb-4 animate-fade-in">
                Welcome to My Portfolio
              </h1>
              <p className="text-xl md:text-2xl mb-8 animate-fade-in-delayed">
                Hi, I'm [Your Name], a [Your Profession].
              </p>
              <div className="flex justify-center">
                <div
                  className="group w-40 h-12 bg-white text-blue-600 font-semibold rounded-lg shadow-lg transition-all duration-300 ease-in-out hover:w-64 hover:h-48 cursor-pointer flex items-start justify-center"
                  onClick={() => setCurrentSection('about')}
                >
                  <div className="flex flex-col items-center w-full h-full">
                    <span className="text-lg mt-3 group-hover:text-white group-hover:mb-4 transition-all duration-300">
                      About Me
                    </span>
                    <div className="opacity-0 group-hover:opacity-100 transition-opacity duration-300 w-full px-6">
                      <p className="text-sm text-purple-600">
                        I'm [Your Name], a passionate [Your Profession] with experience in [Your Skills/Field]. I specialize in creating innovative solutions and have a strong background in [Your Background].
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* About Section */}
        {currentSection === 'about' && (
          <section id="about" className="py-20 bg-gray-50">
            <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
              <h2 className="text-4xl font-bold text-gray-800 mb-6 text-center">
                About Me
              </h2>
              <p className="text-lg text-gray-600 leading-relaxed">
                Write about your background, skills, and achievements here.
              </p>
            </div>
          </section>
        )}

        {/* Portfolio Section */}
        {currentSection === 'portfolio' && (
          <section id="portfolio" className="py-20 bg-white">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
              <h2 className="text-4xl font-bold text-gray-800 mb-10 text-center">
                My Projects
              </h2>
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {projects.map((project, index) => (
                  <ProjectCard
                    key={index}
                    title={project.title}
                    description={project.description}
                    link={project.link}
                    image={project.image}
                  />
                ))}
              </div>
            </div>
          </section>
        )}

        {/* Resume Section */}
        {currentSection === 'resume' && (
          <section id="resume" className="py-20 bg-gray-50">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
              <h2 className="text-4xl font-bold text-gray-800 mb-10 text-center">
                Resume
              </h2>
              <Resume />
            </div>
          </section>
        )}

        {/* Contact Section */}
        {currentSection === 'contact' && (
          <section id="contact" className="py-20 bg-white">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
              <h2 className="text-4xl font-bold text-gray-800 mb-10 text-center">
                Contact Me
              </h2>
              <ContactForm />
            </div>
          </section>
        )}
      </main>

      {/* Footer */}
      <footer className="bg-gray-800 text-white py-6">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <p>Â© 2025 [Your Name]. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
